mapper.select:
select if(count(*), true, false)
from tb_user
where root = 1;

select id
from tb_user
where open_id = #{openId}
and status = 1;

select p.permission_name
from tb_user u
join tb_role r on json_contains(u.role, cast(r.id as char))
join tb_permission p on json_contains(r.permissions, cast(p.id as char))
where u.id = #{userId}
and u.status = 1;
